package practice_if_20250718;

public class If_practice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 점수가 90점 이상이면 수, 80점 이상이면 우... 60점 이상이면 가를 출력하는 프로그램
		
		// 점수가 70점 이상이면 합격 70점 미만이면 불합격을 출력하는 프로그램
		
		int score = 50;
		
		if (score >= 70) {
			System.out.println("합격");
			
		} else {
			System.out.println("불합격 했으니 저녁에 술마셔서 마음이라도 합격 예정되신거");
		}
		
		System.out.println("축하합니다");
	}

}
